import { useState } from 'react'
import { Formik, Form, Field } from 'formik'

const App = () => {
  const [photos, setPhotos] = useState([])
  console.log(photos)
  const open = (url) => window.open(url)

  return (
    <>
      <header className='p-2 bg-sky-700'>
        <Formik
          initialValues={{ search: '' }}
          onSubmit={async values => {
            const response = await fetch(`https://api.unsplash.com/search/photos?per_page=20&query=${values.search}`, {
              headers: {
                'Authorization': 'Client-ID qvy2y7fRCE_mIZrDJ8fevnKO8Mm29ZYhiXQSRq70mOw'
              }
            })
            const data = await response.json()
            setPhotos(data.results)
          }}
        >
          <Form>
            <Field name='search' placeholder="Busca una imagen" className="h-14 w-96 pl-10 pr-20 rounded-lg z-0 focus:shadow focus:outline-none"/>
          </Form>
        </Formik>
      </header>
      <div className="container mx-auto">

       <article className="grid grid-cols-4 gap-4">
        {photos.map(photo => 
            <div className="max-w-sm rounded overflow-hidden shadow-lg"  key={photo.id} onClick={() => open(photo.links.html)}>
              <img src={photo.urls.regular} alt="{props}"/>
                  <div className="px-6 py-4">
                    <div className="font-bold text-xl mb-2">{photo.user.name}</div>
                    <p className="text-gray-700 text-base">{[photo.description, photo.alt_description].join(' - ')}</p>
                  </div>
            </div>
          )}
       </article>
      </div>
    </>
  );
}

export default App;
